// Fixed Parse & Group Data for Wynn Las Vegas CSV structure
const rows = $input.all().map(i => i.json);

console.log("=== PROCESSING WYNN DATA ===");
console.log("Total rows:", rows.length);

// Helper functions
const norm = (s) => (s == null ? "" : String(s).trim());
const cleanDomain = (u = "") =>
  norm(u).replace(/^https?:\/\//i, "").replace(/^www\./i, "").split("/")[0] || null;

// Find the Brands row - handle BOM character in column name
const brandsRow = rows.find(row => {
  // Check both possible column names (with and without BOM)
  const tableName1 = row["Table name"] || "";
  const tableName2 = row["﻿Table name"] || ""; // BOM character version

  return String(tableName1).toLowerCase() === "brands" || 
         String(tableName2).toLowerCase() === "brands";
});

if (!brandsRow) {
  throw new Error(`No brands row found. Available table names: ${rows.map(r => r["Table name"] || r["﻿Table name"]).join(", ")}`);
}

console.log("Found brands row:", brandsRow);

// Get the record data - handle both column name variations
const recordDataRaw = brandsRow["Record data"] || brandsRow["﻿Record data"];

if (!recordDataRaw) {
  throw new Error("No record data found in brands row");
}

// Parse the JSON record data
let brandData;
try {
  brandData = typeof recordDataRaw === "string" ? JSON.parse(recordDataRaw) : recordDataRaw;
} catch (e) {
  throw new Error(`Failed to parse brands record data: ${e.message}`);
}

console.log("Parsed brand data:", brandData);

// Extract the brand information
const company = norm(brandData.name) || null;
const competitors = Array.isArray(brandData.competitors) ? brandData.competitors.map(norm).filter(Boolean) : [];
const industry = norm(brandData.industry) || null;
const domain = cleanDomain(brandData.brand_url || "");
const positioning = norm(brandData.positioning) || null;
const description = norm(brandData.description) || null;
const voice = norm(brandData.voice) || null;
const values = norm(brandData.values) || null;

// Create comprehensive whitelist
const whitelist = Array.from(new Set([company, ...competitors].filter(Boolean)));

console.log("Extracted data:");
console.log("- Company:", company);
console.log("- Competitors:", competitors);
console.log("- Industry:", industry);
console.log("- Whitelist:", whitelist);

// Build enhanced context for dynamic scenario generation
const businessContext = {
  industry_type: industry,
  positioning_statement: positioning,
  brand_description: description,
  brand_voice: voice,
  brand_values: values,
  competitive_set: whitelist,
  market_focus: industry === "Hotels & Resorts" ? "luxury hospitality and guest experience" : "business solutions and customer experience"
};

return [{
  json: {
    company,
    competitors,
    industry,
    domain,
    positioning,
    description,
    voice,
    values,
    whitelist,
    business_context: businessContext,
    // Keep original data for debugging
    debug_info: {
      found_brands_row: true,
      parsed_successfully: true,
      total_rows_processed: rows.length
    }
  }
}];

// Competitor Research Node - Research additional competitors if we have fewer than 10
const inputData = $json;

console.log("=== COMPETITOR RESEARCH ===");
console.log("Current competitors:", inputData.competitors?.length || 0);
console.log("Company:", inputData.company);
console.log("Industry:", inputData.industry);

// Check if we need to research more competitors
const currentCompetitors = inputData.competitors || [];
const minCompetitors = 10;

if (currentCompetitors.length >= minCompetitors) {
  console.log("Sufficient competitors found, no research needed");
  return [{ json: inputData }];
}

const company = inputData.company || "";
const industry = inputData.industry || "";
const competitorsNeeded = minCompetitors - currentCompetitors.length;

console.log(`Need to research ${competitorsNeeded} additional competitors`);

// Create research prompt for additional competitors
const researchPrompt = {
  system_content: `You are a competitive intelligence researcher. Research additional competitors in the ${industry} industry for ${company}. Return ONLY valid JSON in the exact format specified.`, 
  user_content: `COMPETITOR RESEARCH REQUEST

Company: ${company}
Industry: ${industry}
Current Competitors: ${currentCompetitors.join(", ")}

Research ${competitorsNeeded} additional competitors in the ${industry} industry that would be relevant for competitive analysis of ${company}.

Requirements:
- Focus on direct competitors in the same market segment
- Include both established and emerging competitors
- Prioritize companies with similar positioning or target market
- Do NOT include companies that are not direct competitors
- Do NOT make up or invent companies
- Only include real, verifiable companies

Return ONLY valid JSON in this EXACT format:
{
  "additional_competitors": [
    {
      "company_name": "Real Company Name",
      "industry_segment": "luxury_hospitality",
      "competitive_relevance": "direct_competitor",
      "market_position": "premium",
      "research_confidence": "high|medium|low",
      "verification_notes": "Brief note on why this is a relevant competitor"
    }
  ],
  "research_metadata": {
    "total_researched": ${competitorsNeeded},
    "research_timestamp": "${new Date().toISOString()}",
    "industry_focus": "${industry}",
    "target_company": "${company}"
  }
}

Focus on real companies that would actually compete with ${company} in the ${industry} industry.`
};

return [{ json: { ...inputData, competitor_research_needed: true, research_prompt: researchPrompt } }];

