const html = $json.html_content ?? $json.html ?? $json.content;
if (!html) throw new Error('No HTML content found');

const rawFilename = ($json.filename && String($json.filename).trim()) || 'competitive-report.html';

// Use the actual filename instead of overwriting index.html
