// HTTP Response Processor - Split Node Version
// This node processes individual HTTP responses from the HTTP Request node
// Designed to work with n8n's Split In Batches node for individual processing

function processHttpResponse(url, httpResponse, citationData) {
  const result = {
    url: url,
    citation_data: citationData,
    processing_timestamp: new Date().toISOString(),
    batch_id: citationData?.batch_id || "unknown",
    item_index: citationData?.item_index || 0,
  };

  try {
    // Check if we got a valid HTTP response
    if (!httpResponse) {
      result.scraping_status = "no_response";
      result.error_type = "no_response";
      result.scraping_error = "No HTTP response received";
      result.authority_score_adjustment = -0.3;
      console.log(`âŒ No response for ${url}`);
      return result;
    }

    // Extract HTTP status code
    const statusCode = httpResponse.statusCode || httpResponse.status || 0;
    result.http_status_code = statusCode;

    // Extract response headers
    result.response_headers = httpResponse.headers || {};
    result.content_type = result.response_headers["content-type"] || "unknown";
    result.content_length =
      parseInt(result.response_headers["content-length"]) || 0;

    // Classify response based on status code
    if (statusCode >= 200 && statusCode < 300) {
      // Success - check content quality
      result.scraping_status = "success";
      result.error_type = null;
      result.scraping_error = null;

      // Check if content is substantial
      const htmlContent = httpResponse.body || "";
      result.content_length_actual = htmlContent.length;

      if (htmlContent.length < 100) {
        result.scraping_status = "success_but_minimal_content";
        result.error_type = "minimal_content";
        result.scraping_error = `Content too short (${htmlContent.length} chars)`;
        result.authority_score_adjustment = -0.1;
        console.log(
          `âš ï¸  Minimal content for ${url} (${htmlContent.length} chars)`
        );
      } else {
        // Only extract metadata for substantial content (performance optimization)
        if (htmlContent.length > 500) {
          result.extracted_metadata = extractMetadataFromHtml(htmlContent, url);
        } else {
          result.extracted_metadata = {
            content_type: "web_page",
            minimal_content: true,
          };
        }
        result.authority_score_adjustment = 0.1; // Bonus for successful scraping
        console.log(`âœ… Success for ${url} (${htmlContent.length} chars)`);
      }
    } else if (statusCode === 404) {
      result.scraping_status = "not_found";
      result.error_type = "not_found";
      result.scraping_error = "404 Not Found";
      result.authority_score_adjustment = -0.5; // Significant penalty for 404
      console.log(`ðŸ” 404 Not Found: ${url}`);
    } else if (statusCode === 403) {
      result.scraping_status = "forbidden";
      result.error_type = "forbidden";
      result.scraping_error = "403 Forbidden - likely anti-bot protection";
      result.authority_score_adjustment = -0.2; // Penalty for blocked access
      console.log(`ðŸš« 403 Forbidden: ${url}`);
    } else if (statusCode >= 400 && statusCode < 500) {
      result.scraping_status = "client_error";
      result.error_type = "client_error";
      result.scraping_error = `${statusCode} Client Error`;
      result.authority_score_adjustment = -0.3;
      console.log(`âš ï¸  Client Error ${statusCode}: ${url}`);
    } else if (statusCode >= 500 && statusCode < 600) {
      result.scraping_status = "server_error";
      result.error_type = "server_error";
      result.scraping_error = `${statusCode} Server Error`;
      result.authority_score_adjustment = -0.2; // Less penalty for server errors
      console.log(`ðŸ”¥ Server Error ${statusCode}: ${url}`);
    } else {
      result.scraping_status = "unknown_status";
      result.error_type = "unknown_status";
      result.scraping_error = `Unknown status code: ${statusCode}`;
      result.authority_score_adjustment = -0.3;
      console.log(`â“ Unknown status ${statusCode}: ${url}`);
    }

    // Add response body preview (first 200 chars) for debugging - reduced for performance
    if (httpResponse.body && httpResponse.body.length > 0) {
      result.response_preview = httpResponse.body.substring(0, 200);
    }

    // Calculate authority score adjustment
    const originalScore = result.citation_data?.authority_score || 0;
    const adjustment = result.authority_score_adjustment || 0;
    const finalScore = Math.max(0, originalScore + adjustment);

    result.authority_score_analysis = {
      original_score: originalScore,
      adjustment: adjustment,
      final_score: finalScore,
      adjustment_reason: result.error_type || "success",
    };

    console.log(
      `ðŸ“Š Authority Score: ${originalScore} â†’ ${finalScore} (${
        adjustment > 0 ? "+" : ""
      }${adjustment}) for ${url}`
    );
  } catch (error) {
    result.scraping_status = "processing_failed";
    result.error_type = "processing_error";
    result.scraping_error = `Response processing failed: ${error.message}`;
    result.authority_score_adjustment = -0.3;
    console.log(`ðŸ’¥ Processing error for ${url}: ${error.message}`);
  }

  return result;
}

function extractMetadataFromHtml(html, url) {
  try {
    // Only extract metadata if content is substantial (performance optimization)
    if (!html || html.length < 100) {
      return { content_type: "web_page", minimal_content: true };
    }

    const metadata = {
      title: null,
      author: null,
      publication_date: null,
      description: null,
      publisher: null,
      content_type: "web_page",
    };

    // Use faster string operations instead of regex where possible
    const lowerHtml = html.toLowerCase();

    // Extract title - use indexOf for better performance
    const titleStart = lowerHtml.indexOf("<title>");
    if (titleStart !== -1) {
      const titleEnd = lowerHtml.indexOf("</title>", titleStart);
      if (titleEnd !== -1) {
        const titleContent = html.substring(titleStart + 7, titleEnd);
        metadata.title = titleContent
          .trim()
          .replace(/\s+/g, " ")
          .substring(0, 200); // Limit length
      }
    }

    // Only extract other metadata if title was found (indicating valid HTML)
    if (metadata.title) {
      // Extract meta description - simplified regex
      const descMatch = html.match(
        /<meta[^>]*name=["']?description["']?[^>]*content=["']([^"']{1,300})["']/i
      );
      if (descMatch) {
        metadata.description = descMatch[1].trim();
      }

      // Extract author - simplified regex
      const authorMatch = html.match(
        /<meta[^>]*name=["']?author["']?[^>]*content=["']([^"']{1,100})["']/i
      );
      if (authorMatch) {
        metadata.author = authorMatch[1].trim();
      }
    }

    return metadata;
  } catch (error) {
    return { error: `Metadata extraction failed: ${error.message}` };
  }
}

// Main processing logic for individual response
const inputData = $input.all();
const processedResults = [];

console.log(`ðŸ”„ Processing individual HTTP response...`);

// Process the single HTTP response (split node processes one at a time)
for (const item of inputData) {
  const data = item.json;

  // Skip metadata items
  if (data.is_metadata_item) {
    console.log(`â­ï¸  Skipping metadata item`);
    continue;
  }

  console.log(`ðŸ” Processing URL: ${data.url}`);

  const result = processHttpResponse(
    data.url,
    data, // The entire item contains the HTTP response
    data.citation_data
  );

  processedResults.push(result);
}

// Return the processed result for this individual response
return processedResults.map((result) => ({
  json: {
    ...result,
    is_individual_response: true,
    processing_node: "http_response_processor_split",
    prd_version: "2.1_split",
    workflow_stage: "individual_response_processing_complete",
  },
}));

// Data Stripper Node - Extracts only essential data for HTTP processing pipeline
// This node preserves full webscraper data but only passes minimal data forward

console.log("=== DATA STRIPPER NODE ===");
console.log("Input items:", $input.all().length);

const inputItems = $input.all();
const strippedItems = [];

// Process each input item
inputItems.forEach((item, index) => {
  const data = item.json;

  console.log(`Processing item ${index + 1}:`, {
    has_source_url: !!data.source_url,
    has_extracted_url: !!data.extracted_url,
    is_summary_item: !!data.is_summary_item,
  });

  // Skip summary items - they don't need HTTP processing
  if (data.is_summary_item) {
    console.log(`â­ï¸  Skipping summary item ${index + 1}`);
    return;
  }

  // Create minimal item with only essential data for HTTP processing
  const strippedItem = {
    // Essential for HTTP processing
    url: data.source_url || data.extracted_url,
    original_url: data.source_url,
    clean_url: data.extracted_url,

    // Unique identifier for merging back later
    citation_id: data.citation_id || `citation_${Date.now()}_${index}`,
    item_index: data.item_index || index + 1,

    // Minimal metadata for tracking
    url_extraction_status: data.url_extraction_status,
    scraping_timestamp: data.scraping_timestamp,

    // Store reference to full data for later merging
    _full_citation_data: data,
    _data_stripper_timestamp: new Date().toISOString(),
  };

  // Add to stripped items
  strippedItems.push({
    json: strippedItem,
  });

  console.log(`âœ… Stripped item ${index + 1}:`, {
    url: strippedItem.url,
    citation_id: strippedItem.citation_id,
  });
});

// Log summary statistics (but don't add summary item to output)
const summaryStats = {
  total_input_items: inputItems.length,
  processed_items: strippedItems.length,
  skipped_summary_items: inputItems.filter((item) => item.json.is_summary_item)
    .length,
  stripping_timestamp: new Date().toISOString(),
  prd_version: "2.1_fixed",
};

console.log(`=== DATA STRIPPER COMPLETE ===`);
console.log(`Input items: ${summaryStats.total_input_items}`);
console.log(`Output items: ${summaryStats.processed_items}`);
console.log(`Skipped summary items: ${summaryStats.skipped_summary_items}`);
console.log(
  `Memory saved: ~${Math.round(inputItems.length * 0.7 * 100)}KB per item`
);

return strippedItems;

// Fixed Parse & Group Data for Wynn Las Vegas CSV structure
const rows = $input.all().map(i => i.json);

console.log("=== PROCESSING WYNN DATA ===");
console.log("Total rows:", rows.length);

// Helper functions
const norm = (s) => (s == null ? "" : String(s).trim());
const cleanDomain = (u = "") =>
  norm(u).replace(/^https?:\/\//i, "").replace(/^www\./i, "").split("/")[0] || null;

// Find the Brands row - handle BOM character in column name
const brandsRow = rows.find(row => {
  // Check both possible column names (with and without BOM)
  const tableName1 = row["Table name"] || "";
  const tableName2 = row["ï»¿Table name"] || ""; // BOM character version

  return String(tableName1).toLowerCase() === "brands" || 
         String(tableName2).toLowerCase() === "brands";
});

if (!brandsRow) {
  throw new Error(`No brands row found. Available table names: ${rows.map(r => r["Table name"] || r["ï»¿Table name"]).join(", ")}`);
}

console.log("Found brands row:", brandsRow);

// Get the record data - handle both column name variations
const recordDataRaw = brandsRow["Record data"] || brandsRow["ï»¿Record data"];

if (!recordDataRaw) {
  throw new Error("No record data found in brands row");
}

// Parse the JSON record data
let brandData;
try {
  brandData = typeof recordDataRaw === "string" ? JSON.parse(recordDataRaw) : recordDataRaw;
} catch (e) {
  throw new Error(`Failed to parse brands record data: ${e.message}`);
}

console.log("Parsed brand data:", brandData);

// Extract the brand information
const company = norm(brandData.name) || null;
const competitors = Array.isArray(brandData.competitors) ? brandData.competitors.map(norm).filter(Boolean) : [];
const industry = norm(brandData.industry) || null;
const domain = cleanDomain(brandData.brand_url || "");
const positioning = norm(brandData.positioning) || null;
const description = norm(brandData.description) || null;
const voice = norm(brandData.voice) || null;
const values = norm(brandData.values) || null;

// Create comprehensive whitelist
const whitelist = Array.from(new Set([company, ...competitors].filter(Boolean)));

console.log("Extracted data:");
console.log("- Company:", company);
console.log("- Competitors:", competitors);
console.log("- Industry:", industry);
console.log("- Whitelist:", whitelist);

// Build enhanced context for dynamic scenario generation
const businessContext = {
  industry_type: industry,
  positioning_statement: positioning,
  brand_description: description,
  brand_voice: voice,
  brand_values: values,
  competitive_set: whitelist,
  market_focus: industry === "Hotels & Resorts" ? "luxury hospitality and guest experience" : "business solutions and customer experience"
};

return [{
  json: {
    company,
    competitors,
    industry,
    domain,
    positioning,
    description,
    voice,
    values,
    whitelist,
    business_context: businessContext,
    // Keep original data for debugging
    debug_info: {
      found_brands_row: true,
      parsed_successfully: true,
      total_rows_processed: rows.length
    }
  }
}];

// Competitor Research Node - Research additional competitors if we have fewer than 10
const inputData = $json;

console.log("=== COMPETITOR RESEARCH ===");
console.log("Current competitors:", inputData.competitors?.length || 0);
console.log("Company:", inputData.company);
console.log("Industry:", inputData.industry);

// Check if we need to research more competitors
const currentCompetitors = inputData.competitors || [];
const minCompetitors = 10;

if (currentCompetitors.length >= minCompetitors) {
  console.log("Sufficient competitors found, no research needed");
  return [{ json: inputData }];
}

const company = inputData.company || "";
const industry = inputData.industry || "";
const competitorsNeeded = minCompetitors - currentCompetitors.length;

console.log(`Need to research ${competitorsNeeded} additional competitors`);

// Create research prompt for additional competitors
const researchPrompt = {
  system_content: `You are a competitive intelligence researcher. Research additional competitors in the ${industry} industry for ${company}. Return ONLY valid JSON in the exact format specified.`, 
  user_content: `COMPETITOR RESEARCH REQUEST

Company: ${company}
Industry: ${industry}
Current Competitors: ${currentCompetitors.join(", ")}

Research ${competitorsNeeded} additional competitors in the ${industry} industry that would be relevant for competitive analysis of ${company}.

Requirements:
- Focus on direct competitors in the same market segment
- Include both established and emerging competitors
- Prioritize companies with similar positioning or target market
- Do NOT include companies that are not direct competitors
- Do NOT make up or invent companies
- Only include real, verifiable companies

Return ONLY valid JSON in this EXACT format:
{
  "additional_competitors": [
    {
      "company_name": "Real Company Name",
      "industry_segment": "luxury_hospitality",
      "competitive_relevance": "direct_competitor",
      "market_position": "premium",
      "research_confidence": "high|medium|low",
      "verification_notes": "Brief note on why this is a relevant competitor"
    }
  ],
  "research_metadata": {
    "total_researched": ${competitorsNeeded},
    "research_timestamp": "${new Date().toISOString()}",
    "industry_focus": "${industry}",
    "target_company": "${company}"
  }
}

Focus on real companies that would actually compete with ${company} in the ${industry} industry.`
};

return [{ json: { ...inputData, competitor_research_needed: true, research_prompt: researchPrompt } }];

