// Ensure Valid JSON (Prompt 31) â€” robust extractor for Claude in n8n
// Works with either the Anthropic Messages node or a raw HTTP Request node.

const resp = $input.first().json;  // <-- read from upstream input

function getText(r) {
  if (!r) return "";
  if (typeof r === "string") return r;

  // Anthropic Messages (n8n node) common shapes
  if (r.content?.[0]?.text) return r.content[0].text;                     // { content: [{text}] }
  if (r.response?.content?.[0]?.text) return r.response.content[0].text;  // { response: { content: [{text}] } }
  if (r.messages?.[0]?.content?.[0]?.text) return r.messages[0].content[0].text;

  // Raw HTTP Request â†’ parsed JSON body (Anthropic /v1/messages)
  if (r?.type === "message" && r.content?.[0]?.text) return r.content[0].text;

  // OpenAI-ish fallback (not expected here, but safe)
  if (r?.choices?.[0]?.message?.content) return r.choices[0].message.content;
  if (r?.data?.[0]?.message?.content) return r.data[0].message.content;

  // Last resort: stringify
  return JSON.stringify(r);
}

let txt = getText(resp);

// Strip accidental code fences if any
txt = String(txt).replace(/^```(?:json)?\s*/i, "").replace(/```$/m, "");

// Find fenced JSON
const m = txt.match(/<<JSON_START>>\s*([\s\S]*?)\s*<<JSON_END>>/);
if (!m) {
  throw new Error("JSON markers not found. First 300 chars: " + txt.slice(0, 300));
}

// Parse and clean control chars if necessary
let obj;
try {
  obj = JSON.parse(m[1]);
} catch {
  const safe = m[1].replace(/[\u0000-\u001F\u007F]/g, "");
  obj = JSON.parse(safe);
}

// Minimal schema checks
if (!Array.isArray(obj.scenarios)) {
  throw new Error("Missing 'scenarios' array.");
}
if (obj.scenarios.length !== 12) {
  throw new Error(`Expected 12 scenarios, got ${obj.scenarios.length}. Increase max_tokens or shorten scenario fields.`);
}
if (!Array.isArray(obj.source_citations)) {
  throw new Error("Missing 'source_citations' array (should be []).");
}

return [{ json: obj }];

// === FORMAT PROMPT 32 with UPDATED ALLOWLIST LOGIC ===
// Updated to allow all domains while maintaining explicit allowlist for reference

// 1) Centralized allowlist of domains (maintained for explicit tracking and reference)
const EXPLICIT_ALLOWLIST = new Set([
  "reddit.com", "www.reddit.com", "old.reddit.com", "redditstatic.com", "redditmedia.com",
  "youtube.com", "www.youtube.com", "m.youtube.com", "youtu.be",
  "wikipedia.org", "en.wikipedia.org",
  "google.com", "www.google.com", "news.google.com", "scholar.google.com",
  "linkedin.com", "www.linkedin.com",
  "facebook.com", "www.facebook.com", "m.facebook.com",
  "quora.com", "www.quora.com", // correct spelling
  "qoura.com", // common misspelling seen in user content
  "medium.com", "www.medium.com",
  "instagram.com", "www.instagram.com"
]);

// 2) Helpers
function safeJSONParse(maybeJSON) {
  if (typeof maybeJSON !== "string") return null;
  try { return JSON.parse(maybeJSON); } catch { return null; }
}

function extractDomain(url) {
  try {
    const u = new URL(url);
    // Strip leading www., keep base host
    return u.hostname.toLowerCase();
  } catch {
    return null;
  }
}

function isAllowedURL(url) {
  const host = extractDomain(url);
  if (!host) return false;

  // Check if this domain is explicitly in our allowlist for tracking purposes
  const isExplicitlyAllowed = EXPLICIT_ALLOWLIST.has(host);
  const isExplicitSubdomain = Array.from(EXPLICIT_ALLOWLIST).some(base => 
    host === base || host.endsWith("." + base)
  );

  // Log explicit domains for tracking purposes
  if (isExplicitlyAllowed || isExplicitSubdomain) {
    console.log(`âœ… Explicitly allowed domain: ${host}`);
  } else {
    console.log(`âœ… Allowing domain (general allow): ${host}`);
  }

  // Return true for all valid domains - we now allow all domains
  return true;
}

function dedupe(arr) {
  return Array.from(new Set(arr));
}

function normalizeCitation(cit) {
  // Ensure keys exist (don't mutate types)
  const out = { ...cit };

  // Normalize URL + domain + verification
  if (out.source_url && typeof out.source_url === "string") {
    const allowed = isAllowedURL(out.source_url);
    if (!allowed) {
      // This should now never happen since we allow all domains, but keeping for safety
      out.allowlist_violation = true;
      out.source_domain = null;
      out.source_url = null;
      if (!out.verification_status || out.verification_status === "verified") {
        out.verification_status = "unverified";
      }
    } else {
      // Allowed: derive domain if missing
      if (!out.source_domain) out.source_domain = extractDomain(out.source_url);
      out.allowlist_violation = false;
    }
  } else {
    // No URL provided
    out.source_domain = out.source_domain || null;
  }

  // Guardrails for enums and basic ranges (soft-fix only if missing)
  if (!out.confidence_level) out.confidence_level = "medium";
  if (!out.verification_status) out.verification_status = "unverified";
  if (typeof out.authority_score !== "number") out.authority_score = 5;
  if (typeof out.claim_impact_score !== "number") out.claim_impact_score = 5;
  if (typeof out.influence_weight !== "number") out.influence_weight = 0.5;
  if (!out.source_origin) out.source_origin = "unknown";

  return out;
}

function collectUrlsFromText(text) {
  if (typeof text !== "string") return [];
  // Simple URL regex for post-hoc auditing (not for normalization)
  const re = /\bhttps?:\/\/[^\s)]+/g;
  const matches = text.match(re) || [];
  return dedupe(matches);
}

// 3) Main pipeline
const results = [];

console.log("=== FORMAT PROMPT 32 DEBUG (UPDATED ALLOWLIST - ALLOW ALL DOMAINS) ===");
console.log("Input items:", $input.all().length);
console.log("Explicit allowlist size:", EXPLICIT_ALLOWLIST.size);
console.log("Note: All domains are now allowed, explicit allowlist maintained for reference");

for (let i = 0; i < $input.all().length; i++) {
  const item = $input.all()[i];
  console.log(`Processing item ${i}:`, Object.keys(item.json || {}));

  let responseText = "";
  let model = "unknown";
  let tokens = 0;

  // Per-scenario diagnostics
  let allowed_urls = [];
  let blocked_urls = [];

  try {
    // ---- Extract raw text payload (supports multiple shapes) ----
    if (item.json?.content?.[0]?.text) {
      // Claude-like
      responseText = item.json.content[0].text;
      model = item.json.model || "claude";
      tokens = item.json.usage?.output_tokens || 0;
    } else if (item.json?.response_text) {
      // Pre-formatted
      responseText = item.json.response_text;
      model = item.json.model || "claude";
      tokens = item.json.tokens_used || 0;
    } else if (typeof item.json === "string") {
      responseText = item.json;
    } else {
      // Fallback to JSON string
      responseText = JSON.stringify(item.json || {});
      console.log("Warning: Unexpected response structure for item", i);
    }

    // ---- Try to parse model output as JSON so we can process citations ----
    const parsed = safeJSONParse(responseText);
    let processedJSON = null;

    if (parsed && parsed.source_citations && Array.isArray(parsed.source_citations)) {
      // Process citations (all domains now allowed)
      const normalizedCitations = parsed.source_citations.map((c) => {
        const before = (c?.source_url ? [c.source_url] : []);
        const norm = normalizeCitation(c);
        const after = (norm?.source_url ? [norm.source_url] : []);

        // Diagnostics - now all URLs should be allowed
        for (const u of before) {
          if (isAllowedURL(u)) { 
            allowed_urls.push(u); 
          } else { 
            blocked_urls.push(u); 
            console.warn(`âš ï¸ Unexpected blocked URL: ${u}`);
          }
        }
        for (const u of after) {
          if (u && isAllowedURL(u)) { allowed_urls.push(u); }
        }
        return norm;
      });

      processedJSON = {
        ...parsed,
        source_citations: normalizedCitations
      };

      // Re-serialize for storage in response_text (so downstream stays consistent)
      responseText = JSON.stringify(processedJSON);
    } else {
      // If not JSON/doesn't contain citations, audit URLs in the text
      const urls = collectUrlsFromText(responseText);
      for (const u of urls) {
        if (isAllowedURL(u)) allowed_urls.push(u);
        else {
          blocked_urls.push(u);
          console.warn(`âš ï¸ Unexpected blocked URL in text: ${u}`);
        }
      }
    }

    // Finalize diagnostics
    allowed_urls = dedupe(allowed_urls);
    blocked_urls = dedupe(blocked_urls);

    results.push({
      scenario_id: i + 1,
      scenario_title: `Scenario ${i + 1}`,
      response_text: responseText,        // JSON string if parseable & processed; otherwise the original text
      tokens_used: tokens,
      model: model,
      timestamp: new Date().toISOString(),
      allowlist_audit: {
        allowed_urls,
        blocked_urls,
        explicit_allowlist_size: EXPLICIT_ALLOWLIST.size,
        policy: "allow_all_domains"
      }
    });

  } catch (error) {
    console.error(`Error processing item ${i}:`, error.message);
    results.push({
      scenario_id: i + 1,
      scenario_title: `Scenario ${i + 1}`,
      response_text: `Error processing response: ${error.message}`,
      tokens_used: 0,
      model: "error",
      timestamp: new Date().toISOString(),
      allowlist_audit: {
        allowed_urls: [],
        blocked_urls: [],
        explicit_allowlist_size: EXPLICIT_ALLOWLIST.size,
        policy: "allow_all_domains"
      }
    });
  }
}

console.log("Results created:", results.length);
console.log("Policy: All domains are now allowed");
console.log("Explicit allowlist maintained for reference and tracking");

return [{ json: { scenarios_completed: results.length, results } }];

// Prompt 32 Formatter - Convert Format Prompt 32 output to merge-compatible format
// Input: { scenarios_completed: 12, results: [...] }
// Output: Structured data compatible with Collect All Data merge

const input = $input.first()?.json || {};

console.log("=== PROMPT 32 FORMATTER DEBUG ===");
console.log("Input keys:", Object.keys(input));
console.log("Scenarios completed:", input.scenarios_completed);
console.log("Results length:", input.results?.length || 0);

// Extract scenarios from the results
const scenarios = input.results || [];

// Function to extract JSON from response_text with aggressive cleaning
function extractJsonFromResponse(responseText, scenarioId) {
  if (!responseText) {
    console.log(`âŒ No response text for scenario ${scenarioId}`);
    return null;
  }

  let jsonString = null;

  // First, try to extract JSON block
  const jsonBlockMatch = responseText.match(/```json\s*([\s\S]*?)```/);
  if (jsonBlockMatch) {
    jsonString = jsonBlockMatch[1].trim();
  } else {
    // Fallback to finding JSON by braces
    const startIndex = responseText.indexOf("{");
    if (startIndex !== -1) {
      let braceCount = 0;
      let endIndex = startIndex;

      for (let i = startIndex; i < responseText.length; i++) {
        if (responseText[i] === "{") {
          braceCount++;
        } else if (responseText[i] === "}") {
          braceCount--;
          if (braceCount === 0) {
            endIndex = i;
            break;
          }
        }
      }

      if (braceCount === 0) {
        jsonString = responseText.substring(startIndex, endIndex + 1);
      }
    }
  }

  if (!jsonString) {
    console.log(`âŒ No JSON found in response text for scenario ${scenarioId}`);
    return null;
  }

  console.log(`ðŸ“‹ Original JSON length for scenario ${scenarioId}:`, jsonString.length);

  // Apply aggressive cleaning specifically targeting the malformed patterns
  let cleaned = jsonString;

  try {
    // Target the specific issues in the sources array
    cleaned = cleaned.replace(/"sources":\s*\[([\s\S]*?)\]/g, (match, sourcesContent) => {
      // Clean the sources array content aggressively
      let cleanedSources = sourcesContent
        // Fix the specific malformed pattern: "url"\," -> "url",
        .replace(/"([^"]*?)"\s*\\\s*,\s*"/g, '"$1",\n    "')
        // Fix pattern at end of array: "url"\, -> "url"
        .replace(/"([^"]*?)"\s*\\\s*,?\s*$/gm, '"$1"')
        // Remove any remaining backslashes before quotes
        .replace(/\\\s*"/g, '"')
        // Remove standalone backslashes
        .replace(/\\\s*,/g, ',')
        // Clean up any remaining backslashes
        .replace(/\\+/g, '')
        // Fix double commas
        .replace(/,\s*,/g, ',')
        // Remove trailing commas
        .replace(/,\s*$/, '')
        // Normalize spacing
        .replace(/"\s*,\s*"/g, '",\n    "')
        // Clean up the beginning and end
        .replace(/^\s*,/, '') // Remove leading comma
        .replace(/,\s*$/, '') // Remove trailing comma
        .trim();

      return `"sources": [\n    ${cleanedSources}\n  ]`;
    });

    // General cleaning
    cleaned = cleaned
      .replace(/\\+/g, '') // Remove all backslashes
      .replace(/,\s*,/g, ',') // Fix double commas
      .replace(/,(\s*[}\]])/g, '$1') // Remove trailing commas before closing
      .trim();

    console.log(`ðŸ§¹ Cleaned JSON length for scenario ${scenarioId}:`, cleaned.length);

    // Try to parse the cleaned JSON
    const parsed = JSON.parse(cleaned);
    console.log(`âœ… Successfully parsed JSON for scenario ${scenarioId}`);
    return parsed;

  } catch (error) {
    console.log(`âŒ JSON parsing failed for scenario ${scenarioId}:`, error.message);

    // For scenarios 1 and 5, try direct string extraction instead of JSON parsing
    if (scenarioId === 1 || scenarioId === 5) {
      console.log("ðŸ”§ Attempting direct string extraction for scenario", scenarioId);

      try {
        // Extract data directly from the original response text using string methods
        const responseText = jsonString;

        // Extract title
        const titleMatch = responseText.match(/"title":\s*"([^"]+)"/);
        const title = titleMatch ? titleMatch[1] : null;

        // Extract description  
        const descMatch = responseText.match(/"description":\s*"([^"]+(?:\\.[^"]*)*?)"/);
        const description = descMatch ? descMatch[1].replace(/\\"/g, '"') : null;

        // Extract competitors_ranked array
        const competitorsMatch = responseText.match(/"competitors_ranked":\s*\[([\s\S]*?)\]/);
        const competitors = [];
        if (competitorsMatch) {
          // Extract each competitor object
          const competitorObjects = competitorsMatch[1].match(/\{[^}]+\}/g) || [];
          for (const compObj of competitorObjects) {
            const companyMatch = compObj.match(/"company":\s*"([^"]+)"/);
            const scoreMatch = compObj.match(/"score":\s*([0-9.]+)/);
            const rationaleMatch = compObj.match(/"rationale":\s*"([^"]+(?:\\.[^"]*)*?)"/);

            if (companyMatch && scoreMatch && rationaleMatch) {
              competitors.push({
                company: companyMatch[1],
                score: parseFloat(scoreMatch[1]),
                rationale: rationaleMatch[1].replace(/\\"/g, '"')
              });
            }
          }
        }

        // Extract analysis_details
        const analysisMatch = responseText.match(/"analysis_details":\s*\{([\s\S]*?)\}(?:\s*,\s*"key_findings")/);
        const analysisDetails = {};
        if (analysisMatch) {
          // Extract each company analysis
          const companyAnalyses = analysisMatch[1].match(/"[^"]+"\s*:\s*\{[^}]+(?:\{[^}]*\}[^}]*)*\}/g) || [];
          for (const analysis of companyAnalyses) {
            const companyNameMatch = analysis.match(/^"([^"]+)":/);
            if (companyNameMatch) {
              const companyName = companyNameMatch[1];

              // Extract summary
              const summaryMatch = analysis.match(/"summary":\s*"([^"]+(?:\\.[^"]*)*?)"/);

              // Extract highlights array
              const highlightsMatch = analysis.match(/"highlights":\s*\[([\s\S]*?)\]/);
              const highlights = [];
              if (highlightsMatch) {
                const highlightMatches = highlightsMatch[1].match(/"([^"]+(?:\\.[^"]*)*?)"/g) || [];
                highlights.push(...highlightMatches.map(h => h.slice(1, -1).replace(/\\"/g, '"')));
              }

              analysisDetails[companyName] = {
                summary: summaryMatch ? summaryMatch[1].replace(/\\"/g, '"') : '',
                highlights: highlights,
                metrics: {} // We could extract this too if needed
              };
            }
          }
        }

        // Extract key_findings array
        const findingsMatch = responseText.match(/"key_findings":\s*\[([\s\S]*?)\]/);
        const keyFindings = [];
        if (findingsMatch) {
          const findingMatches = findingsMatch[1].match(/"([^"]+(?:\\.[^"]*)*?)"/g) || [];
          keyFindings.push(...findingMatches.map(f => f.slice(1, -1).replace(/\\"/g, '"')));
        }

        console.log(`âœ… Direct extraction for scenario ${scenarioId}:`);
        console.log(`  Title: ${title}`);
        console.log(`  Competitors: ${competitors.length}`);
        console.log(`  Analysis companies: ${Object.keys(analysisDetails).length}`);
        console.log(`  Key findings: ${keyFindings.length}`);

        return {
          title: title,
          description: description,
          competitors_ranked: competitors,
          analysis_details: analysisDetails,
          key_findings: keyFindings
        };

      } catch (extractError) {
        console.log(`âŒ Direct extraction also failed for scenario ${scenarioId}:`, extractError.message);
      }
    }

    return null;
  }
}

// Process each scenario to extract structured data
const processedScenarios = [];
const allSources = [];
const allCitations = [];

for (const scenario of scenarios) {
  console.log(`\nðŸ” Processing scenario ${scenario.scenario_id}:`);

  try {
    // Extract JSON from response text
    const parsedResponse = extractJsonFromResponse(scenario.response_text, scenario.scenario_id);

    // Create scenario data
    const scenarioData = {
      scenario_id: scenario.scenario_id,
      scenario_title: parsedResponse?.title || scenario.scenario_title || `Scenario ${scenario.scenario_id}`,
      scenario_description: parsedResponse?.description || "",
      dimension: parsedResponse?.dimension || "unknown",
      user_query: parsedResponse?.user_query || scenario.scenario_title,
      competitors_ranked: parsedResponse?.competitors_ranked || [],
      analysis_details: parsedResponse?.analysis_details || {},
      key_findings: parsedResponse?.key_findings || [],
      response_text: scenario.response_text,
      model: scenario.model,
      tokens_used: scenario.tokens_used,
      timestamp: scenario.timestamp,
    };

    console.log(`ðŸ“Š Scenario ${scenario.scenario_id} results:`);
    console.log(`  Title: ${scenarioData.scenario_title}`);
    console.log(`  Description length: ${scenarioData.scenario_description?.length || 0}`);
    console.log(`  Competitors: ${scenarioData.competitors_ranked?.length || 0}`);
    console.log(`  Analysis companies: ${Object.keys(scenarioData.analysis_details || {}).length}`);
    console.log(`  Key findings: ${scenarioData.key_findings?.length || 0}`);

    processedScenarios.push(scenarioData);

    // Extract sources and citations if available
    if (parsedResponse?.sources) {
      const sources = Array.isArray(parsedResponse.sources) ? parsedResponse.sources : [];
      allSources.push(...sources);
      console.log(`  Sources found: ${sources.length}`);
    }

    if (parsedResponse?.citations) {
      const citations = Array.isArray(parsedResponse.citations) ? parsedResponse.citations : [];
      allCitations.push(...citations);
    }

  } catch (error) {
    console.error(`âŒ Error processing scenario ${scenario.scenario_id}:`, error.message);

    // Create fallback scenario data
    processedScenarios.push({
      scenario_id: scenario.scenario_id,
      scenario_title: scenario.scenario_title || `Scenario ${scenario.scenario_id}`,
      scenario_description: "",
      dimension: "unknown",
      user_query: scenario.scenario_title,
      competitors_ranked: [],
      analysis_details: {},
      key_findings: [],
      response_text: scenario.response_text,
      model: scenario.model,
      tokens_used: scenario.tokens_used,
      timestamp: scenario.timestamp,
      error: error.message,
    });
  }
}

// Create summary statistics
const summaryStats = {
  total_scenarios: processedScenarios.length,
  scenarios_with_rankings: processedScenarios.filter(s => s.competitors_ranked?.length > 0).length,
  scenarios_with_analysis: processedScenarios.filter(s => Object.keys(s.analysis_details || {}).length > 0).length,
  scenarios_with_titles: processedScenarios.filter(s => s.scenario_title && !s.scenario_title.startsWith("Scenario ")).length,
  scenarios_with_descriptions: processedScenarios.filter(s => s.scenario_description && s.scenario_description.length > 0).length,
  total_sources: allSources.length,
  total_citations: allCitations.length,
  processing_timestamp: new Date().toISOString(),
};

console.log("\n=== PROCESSING COMPLETE ===");
console.log("âœ… Processed scenarios:", processedScenarios.length);
console.log("âœ… Scenarios with proper titles:", summaryStats.scenarios_with_titles);
console.log("âœ… Scenarios with descriptions:", summaryStats.scenarios_with_descriptions);
console.log("âœ… Scenarios with rankings:", summaryStats.scenarios_with_rankings);
console.log("âœ… Scenarios with analysis:", summaryStats.scenarios_with_analysis);
console.log("âœ… Sources found:", allSources.length);

// Log successful parsing results
console.log("\n=== SUCCESSFUL PARSING RESULTS ===");
processedScenarios.forEach(scenario => {
  const hasData = scenario.competitors_ranked?.length > 0 || Object.keys(scenario.analysis_details || {}).length > 0;
  const status = hasData ? "âœ… SUCCESS" : "âŒ NO DATA";
  console.log(`${status} - Scenario ${scenario.scenario_id}: ${scenario.scenario_title}`);
});

// Return structured data compatible with Collect All Data merge
return [
  {
    json: {
      // Scenario data
      scenario_rankings: processedScenarios,
      scenarios: processedScenarios, // Alternative key for compatibility

      // Sources and citations (if any were found)
      data_sources: allSources,
      source_citations: allCitations,

      // Summary statistics
      summary_stats: summaryStats,

      // Metadata
      company: input.company || "Report",
      processing_type: "prompt_32_formatter",

      // Original data for debugging
      original_scenarios_completed: input.scenarios_completed,
      original_results_count: input.results?.length || 0,
    },
  },
];

// Source Detail Extractor - Implements Sentaiment PRD v2.0 Source Citation System
// This node extracts granular source details from competitive analysis data

console.log("=== SOURCE DETAIL EXTRACTOR ===");
console.log("Input data:", JSON.stringify($input.first().json, null, 2));

const inputData = $input.first().json;

// Debug: Check what type of data we're receiving
